import React from "react";
import { Link } from "react-scroll";
import { useState } from 'react';
import "./topcontent.css";
import natarajphoto from "../../images/IMG_20231129_112136.jpg";

const Topconent = () => {
  const [pdfUrl, setPdfUrl] = useState('');

  const downloadPdf = async () => {
    try {
      const response = await fetch(pdfUrl);
      const blob = await response.blob();

      const link = document.createElement('a');
      link.href = window.URL.createObjectURL(blob);
      link.download = 'document.pdf';
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
    } catch (error) {
      console.error('Error downloading PDF', error);
    }
  };

  return (
    <div className="container">
      <div className="row">
        <div className="col-lg-12">
          <div className="topcontent">
            <img className="image" src={natarajphoto} />
            <div className="topcontent__container">
              <h1>Mr.Natarajan</h1>
              <p>
                <h5>A Professional Full Stack Web Developer</h5>
              </p>
              <div>
                I am a confident, self driven, passionate, hardworking & fresher
                looking for an opportunity to work in a challenging environment
                and grow up with the organization. I am also fastlearner who
                likes to constantly upgrade myself.
              </div>

              {/* <a
                href="https://www.pdffiller.com/jsfiller-desk21/?mode=ref&lang=en&ref=https%3A%2F%2Fwww.pdffiller.com%2Fen%2Fforms%2Fdashboard&projectId=1404011037&loader=tips&MEDIUM_PDFJS=true&PAGE_REARRANGE_V2_MVP=true&richTextFormatting=true&isPageRearrangeV2MVP=true&jsf-page-rearrange-v2=true&jsf-new-header=false&jsf-document-scroll-zoom=true&jsf-redesign-full=false&routeId=9de5bfb630a542937b498349bb6d7e34#38b787fec0e3422ca23c945e05f21906"
                target="_blank"
              > */}
                <input
                  type="text"
                  placeholder="Enter PDF URL"
                  value={pdfUrl}
                  onChange={(e) => setPdfUrl(e.target.value)}
                />
                <button className="topcontent__downloadbutton" onClick={downloadPdf}>
                  Download cv
                </button>
              {/* </a> */}
              <Link to="projects" smooth={true} duration={500}>
                <button className="topcontent__workbutton">My Work</button>
              </Link>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Topconent;
